import React from 'react';

export default class TasksFilter extends React.Component {
  buttons = [
    {
      name: 'All',
      label: 'All',
    },
    {
      name: 'Active',
      label: 'Active',
    },
    {
      name: 'Complited',
      label: 'Complited',
    },
  ];

  switchFilter = (key) => {
    const { onFilter } = this.props;
    switch (key) {
      case 'All':
        onFilter(key);
        this.setState(() => {
          return {
            buttons: { all: true, active: false, complited: false },
          };
        });
        break;

      case 'Active':
        onFilter(key);
        this.setState(({ buttons }) => {
          return {
            buttons: { all: false, active: true, complited: false },
          };
        });
        break;

      case 'Completed':
        onFilter(key);
        this.setState(({ buttons }) => {
          return {
            buttons: { all: false, active: false, complited: true },
          };
        });
        break;
    }
  };

  render() {
    const { onFilter, onFilterChange } = this.props;

    const buttons = this.buttons.map(({ name, label }) => {
      const isActive = onFilter === name;

      return (
        <li>
          <button
            className={isActive ? 'selected' : ''}
            key={name}
            onClick={() => onFilterChange(name)}
          >
            {label}
          </button>
        </li>
      );
    });
    return <ul className="filters">{buttons}</ul>;
  }
}
