import React from 'react';

export default class TasksFilter extends React.Component {

  state = {
    buttons: {
      all: true, active: false, complited: false
    }
  }



  switchFilter = (key) => {
    const {tasksFilter} = this.props
    switch(key) {
      case 'All':
      tasksFilter(key)
      this.setState(() => {
        return {
          button: {all: true, active: false, complited: false}
        }
      })
      break;

      case 'Active': 
      tasksFilter(key) 
      this.setState(() => {
        return {
          button: {all: true, active: true, complited: false}
        }
      })
    }

  }

  render () {
    return (
      <ul className='filters'>
        <li>
          <button className='selected'
          onClick={this.switchFilter}>All</button>
        </li>
        <li>
          <button
          onClick={this.switchFilter}>Active</button>
        </li>
        <li>
          <button
          onClick={this.switchFilter}>Completed</button>
        </li>
      </ul>
    );
  };
}
