import React from 'react';

export default class TasksFilter extends React.Component {
  state = {
    buttons: {
      all: true,
      active: false,
      complited: false,
    },
  };

  switchFilter = (key) => {
    const { taskFilter } = this.props;
    switch (key) {
      case 'All':
        onFilter(key);
        this.setState(() => {
          return {
            buttons: { all: true, active: false, complited: false },
          };
        });
        break;

      case 'Active':
        onFilter(key);
        this.setState(({ buttons }) => {
          return {
            buttons: { all: false, active: true, complited: false },
          };
        });
        break;

      case 'Completed':
        onFilter(key);
        this.setState(({ buttons }) => {
          return {
            buttons: { all: false, active: false, complited: true },
          };
        });
        break;

    }
  };

  render() {
    const { buttons } = this.state;
    return (
      <ul className="filters">
        <li>
          <button
            className={buttons.all ? 'selected' : ''}
            onClick={() => this.switchFilter('All')}
          >
            All
          </button>
        </li>
        <li>
          <button
            className={buttons.active ? 'selected' : ''}
            onClick={() => this.switchFilter('Active')}
          >
            Active
          </button>
        </li>
        <li>
          <button
            className={buttons.complited ? 'selected' : ''}
            onClick={() => this.switchFilter('Completed')}
          >
            Completed
          </button>
        </li>
      </ul>
    );
  }
}
