import React from 'react';

export default class TasksFilter extends React.Component {
  state = {
    buttons: {
      all: true,
      active: false,
      complited: false,
    },
  };

  switchFilter = (key) => {
    const { onFilter } = this.props;
    switch (key) {
      case 'All':
        onFilter(key);
        this.setState(() => {
          return {
            button: { all: true, active: false, complited: false },
          };
        });
        break;

      case 'Active':
        onFilter(key);
        this.setState(() => {
          return {
            button: { all: false, active: true, complited: false },
          };
        });
        break;

      case 'Complited':
        onFilter(key);
        this.setState(() => {
          return {
            button: { all: false, active: false, complited: true },
          };
        });
        break;
    }
  };

  render() {
    return (
      <ul className="filters">
        <li>
          <button className={buttons.all ? 'selected' : ''} onClick={()=> this.switchFilter('All')}>
            All
          </button>
        </li>
        <li>
          <button className={buttons.active ? 'selected' : ''} onClick={()=> this.switchFilter('Active')}>Active</button>
        </li>
        <li>
          <button className={buttons.selected ? 'selected' : ''} onClick={()=> this.switchFilter('Completed')}>Completed</button>
        </li>
      </ul>
    );
  }
}
